import React, { useState } from "react";
import axios from "axios";

const UploadPage = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [result, setResult] = useState(null);
    const [error, setError] = useState(null);

    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
        setResult(null); // Reset result when a new file is selected
        setError(null);  // Reset error
    };

    const handleUpload = async () => {
        if (!selectedFile) {
            alert("Please select an image to upload.");
            return;
        }

        const formData = new FormData();
        formData.append("image", selectedFile);

        try {
            // Send the image to the backend
            const response = await axios.post("http://localhost:5000/upload", formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });

            // Set the result from the backend
            setResult(response.data);
        } catch (err) {
            console.error("Error uploading file:", err);
            setError("Failed to upload image or get prediction.");
        }
    };

    return (
        <div style={{ padding: "20px", textAlign: "center" }}>
            <h1>Cattle Disease Detection</h1>
            <input
                type="file"
                accept="image/*"
                onChange={handleFileChange}
                style={{ marginBottom: "10px" }}
            />
            <br />
            <button onClick={handleUpload} style={{ padding: "10px 20px", cursor: "pointer" }}>
                Upload and Predict
            </button>
            {result && (
                <div style={{ marginTop: "20px", textAlign: "left" }}>
                    <h3>Prediction Result:</h3>
                    <p><strong>Disease:</strong> {result.disease}</p>
                    <p><strong>Confidence:</strong> {(result.confidence * 100).toFixed(2)}%</p>
                    <p><strong>Remedy:</strong> {result.remedy}</p>
                </div>
            )}
            {error && (
                <div style={{ marginTop: "20px", color: "red" }}>
                    <p>{error}</p>
                </div>
            )}
        </div>
    );
};

export default UploadPage;
