import React, { useState } from "react";
import axios from "axios";

const UploadPage = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [result, setResult] = useState(null);
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(false);

    const handleFileChange = (event) => {
        const file = event.target.files[0];

        // Validate file type
        if (file && !file.type.startsWith("image/")) {
            setError("Please upload a valid image file.");
            setSelectedFile(null);
            return;
        }

        // Validate file size (limit to 5MB)
        if (file && file.size > 5 * 1024 * 1024) {
            setError("File size should not exceed 5MB.");
            setSelectedFile(null);
            return;
        }

        setSelectedFile(file);
        setResult(null); // Reset result
        setError(null);  // Reset error
    };

    const handleUpload = async () => {
        if (!selectedFile) {
            setError("No file selected. Please upload an image.");
            return;
        }

        const formData = new FormData();
        formData.append("image", selectedFile);

        setIsLoading(true); // Show loading indicator
        setError(null);

        try {
            const response = await axios.post("http://localhost:5000/upload", formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });

            setResult(response.data); // Set the prediction result
        } catch (err) {
            console.error("Error uploading file:", err);
            setError("Failed to upload image or get prediction.");
        } finally {
            setIsLoading(false); // Hide loading indicator
        }
    };

    return (
        <div>
            {/* Navbar Section */}
            <nav className="navbar navbar-default probootstrap-navbar">
                <div className="container">
                    <div className="navbar-header">
                        <button
                            aria-controls="navbar"
                            aria-expanded="false"
                            className="navbar-toggle collapsed"
                            data-target="#navbar-collapse"
                            data-toggle="collapse"
                            type="button"
                        >
                            <span className="sr-only">Toggle navigation</span>
                            <span className="icon-bar"></span>
                            <span className="icon-bar"></span>
                            <span className="icon-bar"></span>
                        </button>
                        <a className="navbar-brand" href="#home">
                            Cattle Disease Analysis &amp; Prediction
                        </a>
                    </div>
                    <div className="navbar-collapse collapse" id="navbar-collapse">
                        <ul className="nav navbar-nav navbar-right">
                            <li className="active"><a href="#home">Home</a></li>
                            <li><a href="#about">About us</a></li>
                            <li><a href="#predict">Predict</a></li>
                            <li><a href="#analysis">Preventive Measures</a></li>
                            <li><a href="#feedback">Feedback</a></li>
                        </ul>
                    </div>
                </div>
            </nav>

            {/* Hero Section */}
            <section
                className="probootstrap-hero"
                id="home"
                style={{
                    backgroundImage: "url('https://www.grandmasterglobal.com/blog/wp-content/uploads/2024/03/ai-cowbreeding-1000x560.jpeg')",
                    backgroundSize: "cover",
                    color: "#E0F7FA",
                    textAlign: "center",
                    padding: "100px 0",
                }}
            >
                <h1>Early detection saves lives – and livelihoods</h1>
                <p>
                    <a className="btn btn-primary" href="#predict" role="button">
                        Check Now
                    </a>{" "}
                    <a className="btn btn-default" href="#analysis" role="button">
                        See Analysis
                    </a>
                </p>
            </section>

            {/* Predict Section */}
            <section id="predict" style={{ padding: "50px 20px", textAlign: "center" }}>
                <h2>Predict the Disease</h2>
                <p>Upload an image of your cattle to detect possible diseases.</p>
                <input
                    type="file"
                    accept="image/*"
                    onChange={handleFileChange}
                    style={{ marginBottom: "15px" }}
                />
                <br />
                <button
                    onClick={handleUpload}
                    style={{
                        padding: "10px 20px",
                        cursor: "pointer",
                        backgroundColor: "#007BFF",
                        color: "white",
                        border: "none",
                        borderRadius: "15px",
                        width: "200px",
                    }}
                >
                    {isLoading ? "Processing..." : "Upload and Predict"}
                </button>
                <div style={{ border: "2px solid black", marginTop: "40px", padding: "20px" }}>
                    {result && (
                        <div style={{ textAlign: "left", maxWidth: "500px", margin: "0 auto" }}>
                            <h3>Prediction Result:</h3>
                            <p><strong>Disease:</strong> {result.disease}</p>
                            <p><strong>Confidence:</strong> {(result.confidence * 100).toFixed(2)}%</p>
                            <p><strong>Remedy:</strong> {result.remedy}</p>
                        </div>
                    )}
                </div>
                {error && (
                    <div style={{ marginTop: "20px", color: "red" }}>
                        <p>{error}</p>
                    </div>
                )}
            </section>

            {/* Analysis Section */}
            <section id="analysis" style={{ padding: "50px 20px", textAlign: "center" }}>
                <h2>Preventive Measures</h2>
                <ul style={{ listStyle: "none", padding: 0 }}>
                    <li>Vaccination and Disease Prevention</li>
                    <li>Balanced Nutrition</li>
                    <li>Hygiene and Housing</li>
                    <li>Parasite Control</li>
                    <li>Regular Health Monitoring</li>
                </ul>
            </section>
        </div>
    );
};

export default UploadPage;
